<h2>Products</h2>
<h2>Category: {{cur_category}}</h2>
<div *ngFor="let product of products">
  <app-product-item [product] = "product" [cur_category] = "cur_category" (remove)="onNewTaskRemove($event)"></app-product-item>
</div>
  
