  styleUrls: ['./product-category.component.css']
})
export class ProductCategoryComponent implements OnInit{
  category = [...Categories];
  constructor() { }
  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }
  visible = false;

  toggleCategoryList() {
    this.visible = !this.visible;
  }
}
